mapper.select:
SELECT count(*)
FROM book
WHERE category_small_id=#{category}

SELECT *
FROM book
WHERE category_small_id=#{category}
ORDER BY category_small_id desc, reg_date desc
LIMIT #{offset}, #{limit}

SELECT p.name
FROM publisher AS p, (SELECT publisher_id FROM book WHERE book_id=3) AS bp
WHERE p.publisher_id = bp.publisher_id;
