mapper.select:
SELECT count(*)
FROM user
WHERE is_member = #{isMember}

SELECT user_id, user_status_id, email, password, name, is_member
FROM user
WHERE is_member = #{isMember}

SELECT user_id, user_status_id, email, password, name, is_member
FROM user
WHERE email = #{email} AND is_member = #{isMember}

mapper.update:
UPDATE user
SET password = #{password}
WHERE email = #{email} AND is_member = #{isMember}
